mapper.select:
SELECT u.email AS email, u.emp_no AS empNo, u.name AS username, dp.dept_nm AS deptName, pos.pos_nm AS posName FROM users u INNER JOIN depts dp ON dp.dept_cd = u.dept_cd AND dp.avf <= CURDATE() AND NOT EXISTS ( SELECT 1 FROM depts WHERE dept_cd = dp.dept_cd AND avf <= CURDATE() AND avf > dp.avf ) INNER JOIN positions pos ON pos.pos_cd = u.pos_cd AND pos.avf <= CURDATE() AND NOT EXISTS ( SELECT 1 FROM positions WHERE pos_cd = pos.pos_cd AND avf <= CURDATE() AND avf > pos.avf ) WHERE u.avf <= CURDATE() AND NOT EXISTS ( SELECT 1 FROM users WHERE email = u.email AND avf <= CURDATE() AND avf > u.avf )
AND u.emp_no = #{empNo}
AND u.name = #{name}
AND u.dept_cd = #{deptCd}
AND u.pos_cd = #{posCd}
